import axios from "axios";
import React, { useState, useEffect } from 'react';

import './App.css';


// https://api.weatherapi.com/v1/forecast.json?key=698dc13c77094cf187695058201212&amp;q=London&amp;days=1

function App() {
  const [weather, setWeather] = useState(null);
  const [tempType, setTempType] = useState("C");

  useEffect(() => {
    async function getWeather(){
      try {
        const response = await axios.get("https://api.weatherapi.com/v1/forecast.json?key=66656cd159d3477886c134122232210&q=London&days=1&q=London");
        // console.log(response.data);
        setWeather(response?.data);

      } catch(error){

      }
    }
    getWeather();
  }, [])

  const renderWeatherHour =  (hourWeatherList) => {
    let html = <></>;
    html = hourWeatherList?.map((item) => {
      let time = new Date(item.time);
      var ampm = time >= 12 ? ' pm' : ' am';
      time = time % 12;
      time = time ? time : 12; // the hour '0' should be '12'
      let timeText = time + ampm;
      console.log('time', time.getHours())
      console.log('item?.time', item?.time)
      return(
        <div className="weather-temp">
          <p>{item?.time}</p>
          <img src={item?.condition?.icon}/> 
          <p>{tempType === "C" ? item?.temp_c : item?.temp_f}</p>
        </div>
      )
    })
    return html;
  }
  return (
    <div className="App">
      <h1>Weather App Project</h1>
      <div className="container">
        <div className="weather-container">
          <div className="weather-title-container">
            <div>
              {weather?.location.name}
              {weather?.location.region}
            </div>
            <div>
              <span className="weather-type" onClick={() => setTempType("C")}>C"</span>
              <span className="weather-type" onClick={() => setTempType("F")}>F"</span>
            </div>
          </div>
          <div className="weather-item-container">

            {renderWeatherHour(weather?.forecast.forecastday[0].hour)}
            {/* {console.log(weather?.forecast.forecastday[0].hour)} */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
